import { DefaultTheme } from "styled-components";

const myTheme: DefaultTheme = {
    colors: {
        black: '#444',
        blue: '#00007F',
        gray: '#EFEFEF',
        white: '#FFF',
    },
    borderWide: `3px solid #00007F`,
    mb: '2rem',
    maxWidth: '1000px',
    mbBetweenParagraphs: '1.6rem',
    mobileBreakpoint: '600px'
}

export default myTheme
