import React from 'react'


interface BlockQuoteProps {
    title:string,
    quote:string,
    toggleParagraph: () => {}
}

interface BlockquoteStateProps {
    showParagraph:boolean
}

export class Blockquote extends React.Component {
    constructor(props:BlockQuoteProps){
        super(props);
        this.blockquoteRef = React.createRef()
    }

    state:BlockquoteStateProps = {
        showParagraph:false
    }

    blockquoteRef:any

    handleClickOnBlockquote =  ():void => this.setState({showParagraph: !this.state.showParagraph})

    handleBlockQuoteHeight = (ref:any):void => {
        ref = React.createRef()
        if(ref){
            console.log('ref current', ref)
            // console.log('ref', ref.offsetHeight())
        }
        // storeBlockHeight(blockHeight)
    }

    render() {
        const props = this.props
        return (
            <div>
                <blockquote ref={this.blockquoteRef} onClick={() => handleClickOnBlockquote()}>
                <h2>{props.title}</h2>
                <p>-> {props.quote}</p>
            </blockquote>
            </div>
        )
    }
}

export default Blockquote
