import React from 'react'


interface BlockQuoteProps {
    title:string,
    quote:string
}

interface BlockquoteStateProps {
    showParagraph:boolean
}

export class Blockquote extends React.Component {
    constructor(props:BlockQuoteProps){
        super(props);
        this.blockquoteRef = React.createRef()
    }

    state:BlockquoteStateProps = {
        showParagraph:false
    }

    blockquoteRef:any

    handleClickOnBlockquote =  ():void => this.setState({showParagraph: !showParagraph})

