import styled from 'styled-components'

const QuoteContainer = styled.div`
blockquote{
        border-radius:5px;
        background-color:${props => props.theme.colors.gray};
        border-left: 5px solid ${props => props.theme.colors.blue};
        font-family:"Space Mono", monospace;
        margin:3rem 0;
        padding-left: 2rem;
        h2{
            font-size:2.2rem;
            color: ${props => props.theme.colors.blue};
            margin: 1rem 0;
            padding: 1rem 0 0 0;
        }
        p{
            margin: 0 0 2rem 0;
            padding: 0 0 2rem 0;
            line-height:1.2;
        }
    }
`

interface QuoteProps {
    quote:string,
    title:string,
    answer:any,
}

const Quote:any = (props:QuoteProps) => {
    return (
        <QuoteContainer>
            <blockquote>
                <h2>{props.title}</h2>
                <p>-> {props.quote}</p>
            </blockquote>
            {
                props.answer ?
                props.answer.values.map((paragraph:string) => <p>{paragraph}</p>)
                :
                null
            }
        </QuoteContainer>
    )
}

export default Quote
